class IncreasingTripletSubsequence {
    private val array = arrayOfNulls<Int>(3)
    fun increasingTriplet(nums: IntArray): Boolean {
        println("nums.size ${nums.size}")
        if (nums.size < 3) {
            return false
        }

        for ((idx, num) in nums.withIndex()) {
            array[0] = num
            fillArray(num, nums.slice(idx + 1 until nums.size), 1)

            if (full()) {
                return true
            }
        }

        return false
    }

    private fun full(): Boolean {
        return array.filterNotNull().size == 3
    }

    private fun printArray() {
        array.forEach { v -> print("$v, ") }
        println("\n========================================")
    }

    private fun fillArray(baseValue: Int, slice: List<Int>, arrayIndex: Int) {
        if(slice.isEmpty()) {
            return
        }
        println("baseValue : $baseValue, slice : $slice, arrayIndex : $arrayIndex")
        for ((idx, num) in slice.withIndex()) {
            printArray()

            if(full()) {
                return
            }
            if (baseValue < num) {
                array[arrayIndex] = num
                printArray()
                if (arrayIndex + 1 >= 3) {
                    return
                } else {
                    fillArray(num, slice.slice(idx + 1 until slice.size), arrayIndex + 1)
                }
            }
        }

        array[arrayIndex - 1] = null
//        fillArray(baseValue, slice.slice(1 until slice.size), arrayIndex - 1)
    }


}
